custom.scss:
/* Custom Styles */
*{
	font-family: 'Montserrat', sans-serif;
}
.themevale-header-transparent .navPages .navPages-item>.navPages-action .text {
	color: stencilColor('navPages-transparent-color');
}

.themevale-header-transparent.is-sticky .navPages .navPages-item>.navPages-action .text {
	color: stencilColor('header-transparent-sticky-color');
}
#featured-products .sidebarBlock-heading {
	margin-bottom: 25px;
}
#wishlist-dropdown.is-open {
	margin-top: 0;
	border: none;
	> li {
		border: none;
		.button {
			font-size: 12px;
			font-weight: 400;
			letter-spacing: 0.02em;
			text-transform: capitalize;
			border-color: #d9d9d9;
		}
	}
}
@media(max-width: 480px) {
	.header-logo-text {
		font-size: 1rem;
	}
}

.btn-primary {
	width: 194px;
	height: 45px;
	background-color: #eb6b10;
	color: #ffffff;
	display: inline-flex;
	align-items: center;
	justify-content: space-between;
	padding: 17px 14px 15px 27px;

	.btn-text {
		font-size: 18px;
		text-transform: uppercase;
		font-weight: 700;
		color: #ffffff;
	}

	.btn-icon {
		width: 24px;
    	height: 24px;

		.icon {
			width: 100%;
			height: 100%;
			fill: #ffffff;
		}
	}
}
.page-type-product #sticky_addtocart .productView-title {
	letter-spacing: 0;
}

.themevale_header-layout-6 .themevale_header-top .navUser {
	.navUser-icon {
		width: 28px;
		height: 22px;

		.icon {
			width: 100%;
			height: 100%;
			fill: #ffffff;
		}
	}
}

.themevale_search-custom button[type="submit"] .icon {
	fill: #000000;
	stroke: #000000;
}


@media(min-width: 1025px) {
	.themevale_header-layout-6 .themevale_header-top {
		padding: 0;
		background-color: #eb6b10;
		.container {
			justify-content: flex-end;
			height: 35px;
		}
	}
	.themevale_search-custom #search_category {
		display: none;
	}

	.themevale_header-layout-6 .themevale_header-middle .container {
		height: 92px;
	}

	.headerMiddle-item.text-right {
		.navUser-link {
			height: 46px;
			display: inline-flex;
			align-items: center;
			background-color: #eb6b10;
			width: 257px;
			height: 45px;
			font-size: 18px;
			font-weight: 600;
			color: #ffffff;
			text-transform: uppercase;
			justify-content: space-between;
			padding: 15px 21px 15px 27px;

			.navUser-icon {
				width: 24px;
				height: 24px;
				display: inline-block;

				.icon {
					height: 100%;
					width: 100%;
					fill: #ffffff;
				}
			}
		}
	}

	.navUser-action--currencySelector {
		.image-flag {
			img {
				width: 26px;
				height: 26px;
				border-radius: 50%;
				margin-top: 0;
			}

			& + .text-flag {
				font-size: 18px;
				margin-left: 10px;
			}
		}
		.icon {
			height: 14px;
			width: 14px;
			margin-left: 4px;
		}
	}

	.themevale_header-middle {
		.navUser-item + .navUser-item {
			background-color: #ffffff;
		}

		& .navUser-action {
			height: 46px;
			display: inline-flex;
			align-items: center;
			padding: 14px;
		}
	}

	.navUser-action .navUser-icon, .navUser-action .navUser-text {
		font-size: 13px;
		color: #ffffff;
		font-weight: 500;
	}

	.themevale_header-layout-6 {
		.themevale_header-middle {
			background-color: #eeeeee;
		}
	}

	.themevale_header-layout-6 .themevale_header-bottom {
		background-color: #535353;
	}

	.navPages .navPages-item > .navPages-action {
		color: #ffffff;
	}

	.themevale_allCategories {
		background-color: #000000;
	}

	.themevale_allCategories .themevale_allCategories-text {
		color: #ffffff;
	}

	.themevale_allCategories .themevale_allCategories-text .icon {
		fill: #ffffff;
		stroke: #ffffff;
	}

	.navPages .navPages-item > .navPages-action {
		font-size: 16px;
		font-weight: 700;
	}

	.themevale_allCategories .themevale_allCategories-text .text {
		font-size: 16px;
	}

	.themevale_header-middle {
		.container {
			.headerMiddle-item.text-left {
				width: 25%;
			}
			.headerMiddle-item.text-center {
				width: 32%;
				.navUser-item--quickSearch {
					width: 100%;

					.themevale_search-custom {
						width: 100%;
					}
				}
			}
			.headerMiddle-item.text-right {
				width: 42%;

				.navUser-item--locator {
					.navUser-link {
						width: 240px;
						padding: 15px;
					}
				}
			}
		}
	}

	.themevale_header-layout-6 .navPages-container {
		width: 100%;
		max-width: 1000px;
	}
	.container {
		max-width: 1000px;
		padding: 0;
	}

	.block-text-video .block-text-video--wrap .block-video {
		iframe {
			width: 100%;
		}
	}

	.text-carousel {
		position: absolute;
		top: 50%;
		transform: translateY(-50%);
		width: 100%;
	
		.container {
			padding: 0;
		}
		
		.text-carousel-wrap {
			background-color: #ffffff;
			width: 576px;
			height: 276px;
			padding: 27px 50px 27px 36px;
			
			.text-carousel--title {
				font-size: 32px;
				font-weight: 700;
				color: #000000;
				margin: 0 0 18px 0;
			}
			.text-carousel--content {
				font-size: 16px;
				font-weight: 300;
				line-height: 24px;
				color: #000000;
				margin-bottom: 26px;
			}
			.text-carousel--btn {
				text-align: left;
				display: inline-flex;
				align-items: center;
				justify-content: space-between;
				width: 210px;
				height: 45px;
				padding: 17px 14px 15px 27px;
				
				.carousel-btn--text {
					font-size: 18px;
					text-transform: uppercase;
					font-weight: 700;
					color: #ffffff;
				}
				.text-carousel--icon {
					width: 24px;
					height: 24px;
					
					.icon {
						width: 100%;
						height: 100%;
						fill: #ffffff;
					}
				}
			}
		}
	}
	
	.home-text{
		.container{
			.home-text-wrap{
				.home-text--title{
					text-align: center;
					font-size: 36px;
					font-weight: 700;
					color: #eb6b10;
					line-height: 24px;
					margin: 42px 0 28px;
				}
				.home-text--content{
					font-size: 16px;
					color: #000000;
					font-weight: 300;
					line-height: 25px;
					text-align: center;
					margin-bottom: 28px;
				}		
			}	
		}
	}
	
	.header.is-sticky {
		.text-carousel {
			top: 4%;
			transform: translateY(-4%);
		}
	}
	
	.home-banner {
		.home-banner-wrap {
			position: relative;
			.banner-img {
				width: 100%;
				height: auto;
			}
		}
	}
	
	.featuredCategory {
		padding-bottom: 20px;
		.container {
			.featuredCategory-wrap {
				.featuredCategory-title {
					font-size: 30px;
					font-weight: 700;
					text-align: center;
					color: #000000;
					line-height: 25px;
					margin: 36px 0 41px;
				}
				
				.featuredCategory-block {
					display: flex;
					flex-wrap: wrap;
					justify-content: space-between;
					align-items: center;
					margin: 0 -15px;
					
					.featuredCategory-block-item {
						background-image: url("../img/featured-image.jpg");
						background-repeat: no-repeat;
						background-size: cover;
						padding: 15px;
						width: 47%;
						height: auto;
						position: relative;
						margin: 15px;
	
						.overlay {
							position: absolute;
							left: 0;
							right: 0;
							top: 0;
							bottom: 0;
							background-color: #2b2b2c;
							opacity: 0.8;
						}
						
						.block-item-text {
							z-index: 9;
							position: relative;
							.block-item-text--title {
								font-size: 22px;
								line-height: 25px;
								font-weight: 700;
								color: #ffffff;
								margin-bottom: 10px;
							}
							.block-item-text--content {
								font-size: 17px;
								line-height: 27px;
								font-weight: 500;
								color: #ffffff;
								display: block;
								margin-bottom: 54px;
							}
						}
						.block-item-btn {
							z-index: 9;
						}
					}
				}
			}
		}
	}
	
	.halo-shopByCategory {
		margin-bottom: 30px;
		.shopByCategory-wrap {
			.shopByCategory-title {
				font-size: 30px;
				font-weight: 700;
				color: #000000;
				text-align: center;
				margin-bottom: 36px;
			}
			.shopByCategory-block {
				justify-content: space-between;
				flex-wrap: wrap;
				.shopByCategory-item {
					text-align: center;
					width: 33.333333%;
					margin: 0;
					padding: 0 5px;

					.shopByCategory-item-img {
						width: 100%;
						height: 272px;
						img {
							width: 100%;
							height: 100%;
							object-fit: contain;
						}
					}
					.shopByCategory-item--name {
						font-size: 22px;
						font-weight: 700;
						line-height: 25px;
						color: #000000;
						margin: 18px 0;
						text-align: center;
					}

					.shopByCategory-item-btn {
						width: 100%;
					}
				}
			}
		}
	}
	
	.block-text-video {
		background-color: #535353;
		.block-text-video--wrap {
			display: flex;
			justify-content: space-between;
			flex-wrap: wrap;
			padding: 73px 0 41px;
			.block-text {
				width: 52%;
				
				.block-text-title {
					font-size: 60px;
					line-height: 73px;
					font-weight: 900;
					color: #ffffff;
					margin: -16px 0 24px 0;
				}
				
				.block-text-content {
					font-size: 16px;
					line-height: 25px;
					color: #ffffff;
					font-weight: 300;
					margin-bottom: 33px;
				}
				
				.block-text-btn {
					width: 211px;
				}
			}
	
			.block-video {
				width: 46%;
				text-align: right;

				iframe {
					width: 100%;
				}
			}
		}
	}
	
	.block-cargo {
		padding: 63px 0;
		.block-cargo-title {
			font-size: 30px;
			line-height: 25px;
			font-weight: 700;
			text-align: center;
			margin: 0 0 46px 0;
		}
		.block-cargo-wrap {
			display: flex;
			flex-wrap: wrap;
			justify-content: space-between;
			.block-cargo-left {
				width: 60%;
				display: flex;
				flex-wrap: wrap;
				justify-content: space-between;
				
				.cargo-left-item {
					width: 49%;
					
					.cargoLeft-item-img {
						width: 100%;
						height: auto;
						
						img {
							width: 100%;
							height: 100%;
						}
					}
					.cargoLeft-item-wrap {
						padding: 0 32px;
						.cargoLeft-item-title {
						font-size: 24px;
						line-height: 29px;
						font-weight: 700;
						color: #000000;
						margin: 20px 0 8px 0;
						}
						
						.cargoLeft-item-content {
							font-size: 16px;
							line-height: 25px;
							color: #262626;
							margin-bottom: 20px;
						}
					}                
				}
			}
			.block-cargo-right {
				width: 39%;
				display: flex;
				flex-wrap: wrap;
				justify-content: space-between;
				
				.cargoRight-item {
					width: 49%;
					
					.cargoRight-item-img {
						width: 100%;
						height: auto;
						margin-bottom: 30px;
						
						img {
							width: 100%;
							height: 100%;
						}
					}
				}
				.cargoRight-item-btn {
					width: 100%;
					padding: 0 12px;

					.cargoRight-item-btn--text {
						font-size: 16px;
					}
					.cargoRight-item-btn--icon {
						width: 20px;
						height: 20px;
					}
				}
			}
		}
	}
	
	.featuredAssociation {
		margin-bottom: 28px;
		.featuredAssociation-wrap {
			padding: 20px 0;
			.featuredAssociation-title {
				font-size: 30px;
				color: #000000;
				text-align: center;
				font-weight: 700;
				line-height: 25px;
			}
			.featuredAssociation-block {
				display: block;
				white-space: nowrap;
				margin: 0 -7.5px;
				overflow-x: scroll;
				
				.featuredAssociation-item {
					display: inline-block;
					width: 10%;
					height: auto;
					margin: 15px 7.5px;
					
					img {
						width: 100%;
						height: 100%;
					}
				}
			}
		}
	}
	
	.themevale_footer-top {
		background-color: #eb6b10;
		padding: 0;
		.text-center {
			display: flex;
			justify-content: center;
			align-items: center;
			padding: 15px 0;
			
			.subscription-form-5 {
				display: flex;
				align-items: center;
				
				.form {
					max-width: 100%;
					margin-right: 30px;
				}
				
				.footer-info-heading {
					margin: 0 15px;
					color: #ffffff;
					font-size: 21px;
					font-weight: 700; 
					line-height: 25px;
				}
				
				.form-field {
					.form-prefixPostfix {
						.form-input {
							width: 415px;
							height: 46px;
	
							&::placeholder {
								font-size: 16px;
								color: #999999;
								font-weight: 300;
								line-height: 25px;
	
							}
						}
	
						.button.form-prefixPostfix-button--postfix {
							font-size: 18px;
							font-weight: 700;
							color: #ffffff;
							line-height: 25px;
							width: 116px;
							height: 46px;
							padding: 0;
							margin: 0 0 0 27px;

							&:hover {
								background-color: #ffffff;
								color: #000000;
							}
						}
					}
				}
			}
	
			.item {
				.socialLinks {
					.socialLinks-item {
						.icon {
							background-color: #ffffff;
							&.icon--instagram {
								background: #ffffff;
							}
	
							svg {
								fill: #eb6b10;
								stroke: #eb6b10;
							}
						}
					}
				}
			}
		}

		.socialLinks .socialLinks-item {
			margin-right: 16px;
		}
	}
	
	.themevale_footer-middle {
		.footer-info.col-5 {
			.footer-info-col {
				width: 32%;
				
				.footer-info-list {
					.store-address, .store-phone, .store-email {
						font-size: 16px;
						line-height: 22px;
						color: #262626;
						font-weight: 400;
						
						strong {
							color: #000000;
							display: inline-block;
							margin-right: 10px;
							
						}
					}
	
					& > li a {
						font-size: 16px;
						font-weight: 400;
						line-height: 34px;
						color: #262626;
						display: inline;
						margin-bottom: 0;
						background-size: 0 1px;
						background-repeat: no-repeat;
						transition: background-size .3s;
						background-image: linear-gradient(to right,#f7f7f7,#f7f7f7),linear-gradient(to right,#4c4c4c,#4c4c4c);
						background-position: 100% 100%,0 85%;
						padding-bottom: 4px;

	
						.ordering-icon {
							display: block;
							svg {
								width: 53px;
								height: 35px;
							}
						}
	
						.ordering-address {
							display: block;
						}

						&::before {
							display: none;
						}

						&:hover {
							background-size: 100% 1px;
						}
					}
				}
	
				.footer-info-heading {
					font-size: 21px;
					line-height: 25px;
					font-weight: 700;
					color: #eb6b10;
				}

				&:nth-child(5) {
					ul {
						li {
							a {
								&::before {
									display: none;
								}
								.ordering-address {
									display: inline;
									margin-bottom: 0;
									background-size: 0 1px;
									background-repeat: no-repeat;
									transition: background-size .3s;
									background-image: linear-gradient(to right,#f7f7f7,#f7f7f7),linear-gradient(to right,#4c4c4c,#4c4c4c);
									background-position: 100% 100%,0 85%;
									padding-bottom: 4px;
								}

								&:hover {
									.ordering-address {
										background-size: 100% 1px;
									}
								}
							}
						}
					}
				}
				
			}
		}
	}
	
	.themevale_footer-bottom .wrapper {
		display: block;
		.footer-copyright {
			text-align: center;
	
			.powered-by {
				font-size: 16px;
				font-weight: 400;
				line-height: 22px;
				color: #ffffff;
			}
		}
	}
	
	.card .card-figure:hover .card-figcaption, .card .card-figure:hover .card-figcaption2 {
		display: none;
	}

	.product-listing {
		.card-body {
			.card-title {
				font-size: 21px;
				line-height: 25px;
				font-weight: 700;
				color: #000000;
			}
		}
	}

	.card-img-container:after {
		padding-bottom: 100%;
	}

	.category-textBlock {
		.textBlock-title {
			font-size: 21px;
			line-height: 25px;
			font-weight: 700;
			color: #000000;
		}

		.textBlock-content {
			font-size: 16px;
			color: #000000;
			font-weight: 400;
		}
	}

	.productView-info-value.__part {
		font-size: 16px;
	}

	.productView-details {    
		.productView-title {
			font-size: 35px;
			line-height: 25px;
			font-weight: 700;
			color: #000000;
			
		}
		.productView-shortDescription {
			font-size: 18px;
			color: #000000;
			line-height: 27px;
			font-weight: 400;
			margin-bottom: 30px;
		}
		
		.productView-brand {
			font-size: 14px;
			font-weight: 400;
			color: #000000;
		}

		.productView-code-wrap {
			display: flex;
			align-items: center;
			margin: 22px 0;
			.productView-code {
				display: flex;
				align-items: center;
				margin-right: 32px;

				.productView-info-name {
					font-size: 16px;
					font-weight: 800;
					color: #666666;
					line-height: 25px;
					margin-right: 12px;
				}
				.productView-info-value {
					font-size: 16px;
					font-weight: 800;
					color: #eb6b10;
					line-height: 25px;
					margin-bottom: 0;
				}
			}
		}
		
		.prodcutView-form {
			.prodcutView-form--title {
				font-size: 16px;
				line-height: 25px;
				font-weight: 400;
				color: #000000;
				margin-bottom: 2px;
			}
			.prodcutView-form--heading {
				font-size: 16px;
				font-weight: 900;
				line-height: 25px;
				color: #000000;
				margin: 0 0 20px 0;
			}
			.form {
				.form-fieldset {
					display: flex;
					flex-wrap: wrap;

					.form-field{
						position: relative;
						&::after {
							content: "Required";
							position: absolute;
							font-size: 12px;
							color: #eb6b10;
							top: -20px;
							right: 0;
						}
					}
					
					.form-field:not(:nth-child(5)) {
						margin: 15px 0 21px 0;
						width: 100%;
						
						.form-input {
							width: 100%;
							min-height: 42px;

							&::placeholder {
								font-size: 14px;
								font-weight: 500;
								color: #666666;
								line-height: 25px;
							}
						}
					}
	
					.form-field:nth-child(5) {
						margin: 15px 0 30px 0;
						width: 100%;
						.form-input {
							width: 100%;
							min-height: 110px;
							&::placeholder {
								font-size: 14px;
								font-weight: 500;
								color: #666666;
								line-height: 25px;
							}
						}
					}

					#ask-an-expert-button {
						width: 160px;
						border: none;
					}
				}
			}
		}
	}

	.productView-description {
		.tabs {
			display: flex;
			justify-content: space-between;
			.tab {
				margin-right: 30px;
				&:last-child {
					margin: 0;
				}

				border: none;
				&.is-active {
					.tab-title {
						color: #eb6b10;
					}
					&:after {
