}

@media(max-width: 767px) {
	.home-banner {
		.home-banner-wrap {
			width: auto;
			.text-carousel {
				position: unset;
				transform: unset;
		
				.text-carousel-wrap {
					padding: 16px;
		
					.text-carousel--title {
						font-size: 20px;
						margin: 0 0 4px 0;
					}
					.text-carousel--content {
						font-size: 14px;
					}
				}
			}
			.text-carousel--btn {
				width: 150px;
				height: 40px;
				padding: 10px; 

				.carousel-btn--text {
					font-size: 14px;
				}
				.text-carousel--icon {
					width: 20px;
					height: 20px;
				}
			}
		}
	}

	.home-text {
		margin-top: 10px;

		.container .home-text-wrap .home-text--title {
			line-height: 40px;
		}
	}

	.featuredCategory {
		.container {
			.featuredCategory-wrap {
				.featuredCategory-block {
					.featuredCategory-block-item {
						width: 100%;
						margin: 15px 15px 0 15px;
					}
				}
				.featuredCategory-title {
					font-size: 26px;
				}
			}	
		}
	}
	

	.halo-shopByCategory {
		.shopByCategory-wrap {
			.shopByCategory-block {
				.shopByCategory-item {
					width: 48%;

					.shopByCategory-item-btn {
						width: 100%;

						.shopByCategory-item-btn--text {
							font-size: 16px;
						}
						.shopByCategory-item-btn--icon {
							width: 20px;
							height: 20px;
						}
					}
					.shopByCategory-item--name	{
						font-size: 18px;
					}
				}
			}	

			.shopByCategory-title {
				font-size: 26px;
				margin: 36px 0 41px;
			}				
		}
	}


	.block-text-video {
		.block-text-video--wrap {
			.block-text {
				.block-text-title {
					font-size: 25px;
					line-height: 30px;
				}
				.block-text-content {
					font-size: 14px;
				}
			}
			.block-video {
				iframe {
					width: 100%;
				}
			}
		}
	}

	.block-cargo {
		padding: 30px 0;
		.block-cargo-title {
			font-size: 26px;
			margin: 36px 0 41px;
		}
		.block-cargo-wrap {
			.block-cargo-left {
				.cargo-left-item {
					width: 100%;
					margin-bottom: 20px;
					.cargoLeft-item-wrap {
						padding: 0;
						.cargoLeft-item-title {
							font-size: 20px;
						}
						.cargoLeft-item-btn {
							width: 100%;
							justify-content: center;

							.cargoLeft-item-btn--text {
								margin-right: 22px;
							}
						}
					}
				}
			}

			.block-cargo-right {
				.cargoRight-item {
					width: 100%;
					margin-bottom: 20px;
					.cargoRight-item-img {
						height: auto;
						img {
							width: 100%;
							height: auto;
						}
					} 
					.cargoRight-item-btn {
						width: 100%;
						justify-content: center;
						.cargoRight-item-btn--text {
							margin-right: 22px;
						}
					}
				}
			}
		}
	}

	.featuredAssociation {
		.featuredAssociation-wrap {
			.featuredAssociation-block .featuredAssociation-item {
				width: 23%;
			}

			.featuredAssociation-title {
				font-size: 26px;
			}
		}

	}

	.themevale_footer-middle .footer-info.col-5 .footer-info-col {
		width: 100%;
	}

	.themevale_footer-top {
		.text-center {
			.subscription-form-5 {
				display: block;
				.footer-info-heading {
					margin: 8px 0;
					font-size: 18px;
				}
			}
		}
	}

	.product-listing {
		.card-body {
			.productView-info-value {
				float: none;
			}
		}
	}

	#fytm-block #fytmZip {
		width: auto;
	}

}
