{{#if instagram-carousel '==' 'instagram-carousel-1'}}
<section id="InstagramGallery" class="themevale-section {{padding_top}} {{padding_bottom}} background-gray">
    <div class="container">
        {{#if theme_settings.instagram_title '!=' ''}}
            <h2 class="page-heading {{title-border}}">
                <span class="title">
                   <svg class="icon" aria-hidden="true"><use xlink:href="#icon-instagram"></use></svg>
                    {{theme_settings.instagram_title}}
                </span>
            </h2>
        {{/if}}
        {{#if theme_settings.instagram_description '!=' ''}}
            <p class="page-description">{{theme_settings.instagram_description}}</p>
        {{/if}}    
        {{#if theme_settings.instagram_layout '==' 'app'}}
            <div id="instafeed" class="themevale_instagram-carousel instagram-app">
                {{#if theme_settings.instagram_app_code '!=' ''}}
                    <script src='https://snapppt.com/widgets/widget_loader/{{theme_settings.instagram_app_code}}' defer class='snapppt-widget'></script>
                {{/if}}
            </div>
        {{else}}
            <div id="instafeed" class="themevale_instagram-carousel" data-slick='{
                "arrows": false,
                "dots": true,
                "autoplay": false,
                "infinite": true,
                "slidesToShow": 2,
                "slidesToScroll": 2,
                "mobileFirst": true,
                "nextArrow": "<svg class=&apos;slick-next slick-arrow&apos; aria-label=&apos;Next Instagram Image&apos;><use xlink:href=&apos;#icon-slick-next&apos;></use></svg>", 
                "prevArrow": "<svg class=&apos;slick-prev slick-arrow&apos; aria-label=&apos;Previous Instagram Image&apos;><use xlink:href=&apos;#icon-slick-prev&apos;></use></svg>",
                "responsive": [
                    {
                        "breakpoint": 1024,
                        "settings": {
                            "slidesToShow": 5,
                            "slidesToScroll": 5,
                            "arrows": true,
                            "dots": false
                        }
                    },
                    {
                        "breakpoint": 767,
                        "settings": {
                            "slidesToShow": 4,
                            "slidesToScroll": 4,
                            "arrows": false,
                            "dots": true
                        }
                    },
                    {
                        "breakpoint": 550,
                        "settings": {
                            "slidesToShow": 3,
                            "slidesToScroll": 3,
                            "arrows": false,
                            "dots": true
                        }
                    }
                ]}'>
                {{#if theme_settings.instagram_image_1 '!=' ''}}
                    <a class="instagram-link"
                    {{#if theme_settings.instagram_type '==' 'fancybox'}} data-fancybox="images" aria-label="Open Instagram Image 1 in a popup"{{else}} target="_blank" rel="noopener" aria-label="Open Instagram Image 1 in a new tab"{{/if}} 
                    href="javascript:void(0);" data-href="{{#if theme_settings.instagram_type '==' 'fancybox'}}/product_images/uploaded_images/{{theme_settings.instagram_image_1}}{{else}}{{theme_settings.instagram_link_1}}{{/if}}">
                        <img class="lazyload 123" src="{{cdn 'img/loading.svg'}}" data-src="/product_images/uploaded_images/{{theme_settings.instagram_image_1}}" alt="Instagram Image 1"/>
                    </a>
                {{/if}}

                {{#if theme_settings.instagram_image_2 '!=' ''}}
