                    {{#if theme_settings.instagram_type '==' 'fancybox'}} data-fancybox="images" aria-label="Open Instagram Image 2 in a popup"{{else}} target="_blank" rel="noopener" aria-label="Open Instagram Image 2 in a new tab"{{/if}}
                    href="javascript:void(0)" data-href="{{#if theme_settings.instagram_type '==' 'fancybox'}}/product_images/uploaded_images/{{theme_settings.instagram_image_2}}{{else}}{{theme_settings.instagram_link_2}}{{/if}}">
