components/common/social-links:
{{#or social_media theme_settings.social_icon_google_plus_link theme_settings.social_icon_snapchat_link}}
    <ul class="socialLinks socialLinks--alt">
        {{#if social_media}}
            {{#each social_media}}
                <li class="socialLinks-item">
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank" rel="noopener" aria-label="Open {{display_name}} Social Media in a new tab">
                        Open in a new tab
                        <svg aria-hidden="true"><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                </li>
            {{/each}}
        {{/if}}    
        {{#if theme_settings.social_icon_google_plus_link '!=' ''}}
            <li class="socialLinks-item google-plus">
                <a class="icon icon--google-plus" href="{{theme_settings.social_icon_google_plus_link}}" target="_blank" rel="noopener" aria-label="Open Google Plus Social Media in a new tab">
                    <svg aria-hidden="true"><use xlink:href="#icon-google-plus" /></svg>
                </a>
            </li>
        {{/if}}

        {{#if theme_settings.social_icon_snapchat_link '!=' ''}}
            <li class="socialLinks-item snapchat">
                <a class="icon icon--snapchat" href="{{theme_settings.social_icon_snapchat_link}}" target="_blank" rel="noopener" aria-label="Open Snapchat Social Media in a new tab">
                    <svg aria-hidden="true"><use xlink:href="#icon-snapchat-ghost" /></svg>
                </a>
            </li>
        {{/if}}
    </ul>
{{/or}}

