components/common/currency-selector:
{{#if currency_selector.currencies.length '>' 1}}
<a class="navUser-action navUser-action--currencySelector" href="javascript:void(0)"
    data-dropdown="currencySelection" 
    aria-controls="currencySelection" 
    aria-expanded="false"
>
    <span class="image-flag">{{{currency_selector.active_currency_flag}}}</span> 
    <span class="text-flag">{{lang 'common.currency' code=currency_selector.active_currency_code}}</span>
    <i class="icon" aria-hidden="true"><svg aria-hidden="true"><use xlink:href="#icon-chevron-down" /></svg></i>
</a>
<ul class="dropdown-menu" id="currencySelection" data-dropdown-content aria-hidden="true" tabindex="-1">
    {{#each currency_selector.currencies}}
    <li class="dropdown-menu-item">
        <a href="{{{switch_url}}}" data-currency-code="{{{code}}}"
            {{#if cart_currency_switch_url.length '>' 0 }}
                data-cart-currency-switch-url="{{{cart_currency_switch_url}}}"
            {{/if}}
            data-warning="{{lang 'common.currency_switch_promotion'}}"
        >
            {{#if is_active}}
            {{{flag}}} <strong>{{name}}</strong>
            {{else}}
            {{{flag}}} {{name}}
            {{/if}}
        </a>
    </li>
    {{/each}}
</ul>
{{/if}}

components/common/quick-search-all:
{{{ snippet "forms_search"}}}
<form class="form" action="{{urls.search}}">
    <fieldset class="form-fieldset">
        <div class="form-field">
            <div id="search_category">
                <select name="category" class="select_category">
                    <option value="" selected="">All</option>
                    {{#each categories}}
                        <option value="{{id}}">{{name}}</option>
                        {{#if children}}
                            {{#each children}}
                                <option value="{{id}}">&ndash;&ndash;&ndash; {{name}}</option>
                                {{#if children}}
                                    {{#each children}}
                                        <option value="{{id}}">&ndash;&ndash;&ndash;&ndash;&ndash;&ndash; {{name}}</option>
                                        {{#if children}}
                                            {{#each children}}
                                                {{#if children}}
                                                    <option value="{{id}}">&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash; {{name}}</option>
                                                {{else}}
                                                    <option value="{{id}}">&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash;&ndash; {{name}}</option>
                                                {{/if}}
                                            {{/each}}
                                        {{/if}}
                                    {{/each}}
                                {{/if}}
                            {{/each}}
                        {{/if}}
                    {{/each}}
                </select> 
                <svg class="icon" aria-hidden="true"><use xlink:href="#icon-chevron-down" /></svg>
            </div>
            <input class="form-input" data-search-quick name="search_query" id="search_query" data-error-message="{{lang 'search.error.empty_field'}}" autocomplete="off">
            <button type="submit" aria-label="{{lang 'search.quick_search.button_search'}}"><svg class="icon" aria-hidden="true"><use xlink:href="#icon-custom-search"></use></svg></button>
        </div>
    </fieldset>
</form>
<div class="quickSearchResults" data-bind="html: results"></div>

components/common/social-links:
{{#or social_media theme_settings.social_icon_google_plus_link theme_settings.social_icon_snapchat_link}}
    <ul class="socialLinks socialLinks--alt">
        {{#if social_media}}
            {{#each social_media}}
                <li class="socialLinks-item">
                    <a class="icon icon--{{display_name}}" href="{{url}}" target="_blank" rel="noopener" aria-label="Open {{display_name}} Social Media in a new tab">
                        Open in a new tab
                        <svg aria-hidden="true"><use xlink:href="#icon-{{display_name}}" /></svg>
                    </a>
                </li>
            {{/each}}
        {{/if}}    
        {{#if theme_settings.social_icon_google_plus_link '!=' ''}}
            <li class="socialLinks-item google-plus">
                <a class="icon icon--google-plus" href="{{theme_settings.social_icon_google_plus_link}}" target="_blank" rel="noopener" aria-label="Open Google Plus Social Media in a new tab">
                    <svg aria-hidden="true"><use xlink:href="#icon-google-plus" /></svg>
                </a>
            </li>
        {{/if}}

        {{#if theme_settings.social_icon_snapchat_link '!=' ''}}
            <li class="socialLinks-item snapchat">
                <a class="icon icon--snapchat" href="{{theme_settings.social_icon_snapchat_link}}" target="_blank" rel="noopener" aria-label="Open Snapchat Social Media in a new tab">
                    <svg aria-hidden="true"><use xlink:href="#icon-snapchat-ghost" /></svg>
                </a>
            </li>
        {{/if}}
    </ul>
{{/or}}

